import React, { useState, useEffect, useContext } from 'react';
import NavBar from '../components/navBar';
import { CallContext } from '../context/context';
import './callPageStyle.css';

const CallPage = () => {
  const [title, setTitle] = useState('');
  const [comment, setComment] = useState('');
  const [status, setStatus] = useState('');
  const [priority, setPriority] = useState('');
  const { user, setUser, name, setName } = useContext(CallContext);
  const [deletedCallId, setDeletedCallId] = useState(null);

  const [calls, setCalls] = useState([]);

  const handleDelete = (callId) => {
    const token = localStorage.getItem('token');

    fetch(`http://192.168.0.39:3010/call/${callId}`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
        Authorization: token,
      },
    })
      .then(response => response.json())
      .then(data => {
        console.log('Chamado excluído:', data);
        setDeletedCallId(callId); // Armazena o ID do chamado excluído
      })
      .catch(error => console.error('Erro ao excluir chamado:', error));
  };

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem('user'));
    setUser(userData);
  }, [setUser]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    fetch('http://192.168.0.39:3010/call', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        Authorization: token,
      },
    })
      .then(response => response.json())
      .then(data => setCalls(data.calls))
      .catch(error => console.error('Erro ao buscar chamados:', error));
  }, []);

  useEffect(() => {
    // Verifica se o ID do chamado excluído foi definido e atualiza o estado `calls`
    if (deletedCallId) {
      setCalls(prevCalls => prevCalls.filter(call => call.id !== deletedCallId));
      setDeletedCallId(null); // Reseta o ID do chamado excluído
    }
  }, [deletedCallId]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const newCall = {
      title: title,
      comment: comment,
      status: status,
      priority: priority,
      user: name,
    };

    const token = localStorage.getItem('token');

    fetch('http://192.168.0.39:3010/call', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: token,
      },
      body: JSON.stringify(newCall),
    })
      .then((response) => {
        console.log(response);
        return response.json();
      })
      .then((data) => {
        console.log('Chamado cadastrado:', data);
        console.log(newCall);
        if (user) {
          setCalls(prevCalls => [
            ...prevCalls,
            { ...newCall, id: data.id, user: data.name },
          ]);
        } else {
          console.log('Usuário não está definido.');
        }
      })
      .catch((error) => {
        console.error('Erro ao cadastrar chamado', error.response);
      });

    setTitle('');
    setComment('');
    setStatus('');
    setPriority('');
  };

  return (
    <div>
      <NavBar user={user?.name} />

      <div className='H1'>
        <h1>BEM VINDO AO CALL OPENING</h1>
      </div>
      <div className='H3'>
        <h3>Abra seu chamado :)</h3>
      </div>
      <form onSubmit={handleSubmit} className='formContainer'>
        {/* Restante do código do formulário */}
      </form>

      <div className="getCalls">
        {/* Código para exibir os chamados */}
      </div>
    </div>
  );
};

export default CallPage;
