import React, { useState, useEffect, useContext } from 'react';
import NavBar from '../components/navBar';
import { CallContext } from '../context/context';
import './callPageStyle.css';

const CallPage = () => {
  const [title, setTitle] = useState('');
  const [comment, setComment] = useState('');
  const [status, setStatus] = useState('');
  const [priority, setPriority] = useState('');
  const { user, setUser, name, setName } = useContext(CallContext);
  const [deletedCallId, setDeletedCallId] = useState(null);

  const [calls, setCalls] = useState([]);

  const handleDelete = (callId) => {
    console.log('ID do chamado a ser excluído:', callId);
    const token = localStorage.getItem('token');

    fetch(`http://192.168.0.39:3010/call/${callId}`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
        Authorization: token,
      },
    })
      .then(response => response.json())
      .then(data => {
        console.log('Chamado excluído:', data);
        setDeletedCallId(callId); // Armazena o ID do chamado excluído
      })
      .catch(error => console.error('Erro ao excluir chamado:', error));
  };

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem('user'));
    setUser(userData);
  }, [setUser]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    fetch('http://192.168.0.39:3010/call', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        Authorization: token,
      },
    })
      .then(response => response.json())
      .then(data => setCalls(data.calls))
      .catch(error => console.error('Erro ao buscar chamados:', error));
  }, [deletedCallId]); // Adicione deletedCallId como dependência

  useEffect(() => {
    // Verifica se o ID do chamado excluído foi definido e atualiza o estado `calls`
    if (deletedCallId) {
      setCalls(prevCalls => prevCalls.filter(call => call.id !== deletedCallId));
      setDeletedCallId(null); // Reseta o ID do chamado excluído
    }
  }, [deletedCallId, calls]); // Adicione calls como dependência


  useEffect(() => {
    // Verifica se o ID do chamado excluído foi definido e atualiza o estado `calls`
    if (deletedCallId) {
      setCalls(prevCalls => prevCalls.filter(call => call.id !== deletedCallId));
      setDeletedCallId(null); // Reseta o ID do chamado excluído
    }
  }, [deletedCallId]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const newCall = {
      title: title,
      comment: comment,
      status: status,
      priority: priority,
      user: name,
    };

    const token = localStorage.getItem('token');

    fetch('http://192.168.0.39:3010/call', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: token,
      },
      body: JSON.stringify(newCall),
    })
      .then((response) => {
        console.log(response);
        return response.json();
      })
      .then((data) => {
        console.log('Chamado cadastrado:', data);
        console.log(newCall);
        if (user) {
          setCalls(prevCalls => [
            ...prevCalls,
            { ...newCall, id: data.id, user: data.name },
          ]);
        } else {
          console.log('Usuário não está definido.');
        }
      })
      .catch((error) => {
        console.error('Erro ao cadastrar chamado', error.response);
      });

    setTitle('');
    setComment('');
    setStatus('');
    setPriority('');
  };

  return (
    <div>
      <NavBar user={user?.name} />

      <div className='H1'>
        <h1>BEM VINDO AO CALL OPENING</h1>
      </div>
      <div className='H3'>
        <h3>Abra seu chamado :)</h3>
      </div>
      <form onSubmit={handleSubmit} className='formContainer'>
        <div className='formGroup'>
          <label htmlFor='title'>Título:</label>
          <input
            type='text'
            id='title'
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
        <div className='formGroup'>
          <label htmlFor='comment'>Comentário:</label>
          <input
            type='text'
            id='comment'
            value={comment}
            onChange={(e) => setComment(e.target.value)}
          />
        </div>
        <div className='formGroup'>
          <label htmlFor='status'>Status:</label>
          <input
            type='text'
            id='status'
            value={status}
            onChange={(e) => setStatus(e.target.value)}
          />
        </div>
        <div className='formGroup'>
          <label htmlFor='priority'>Prioridade:</label>
          <input
            type='text'
            id='priority'
            value={priority}
            onChange={(e) => setPriority(e.target.value)}
          />
        </div>
        <button type='submit'>Cadastrar chamado</button>
      </form>

      <div className="getCalls">
  {calls.map(call => (
    <div key={call.id} className="callItem">
      <div className="column">
        <h3>Título:</h3>
        <p>{call.title}</p>
      </div>
      <div>
        <h3>Comentário:</h3>
        <p>{call.comment}</p>
      </div>
      <div>
        <h3>Status:</h3>
        <p>{call.status}</p>
      </div>
      <div>
        <h3>Prioridade:</h3>
        <p>{call.priority}</p>
      </div>
      <div>
        <h3>Usuário:</h3>
        <p>{call && user?.name && user?.name.split(' ')[0]}</p>
      </div>
      <div className="callItemButtom">
        <button onClick={() => handleDelete(call.id)}>Excluir</button>
      </div>
    </div>
  ))}
</div>


    </div>
  );
};

export default CallPage;
