import React, { useState, useEffect } from 'react';

function UpdateOverlay({ onClose, onUpdate }) {
  const [updatedTitle, setUpdatedTitle] = useState('');
  const [updatedComment, setUpdatedComment] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = (CallId) => {


    setIsLoading(true);

    const updatedCall = {
      title: updatedTitle,
      comment: updatedComment,
      // ...outros campos de atualização
    };

    const token = localStorage.getItem('token');
    const callId = 'ID_DO_CHAMADO'; // Substitua 'ID_DO_CHAMADO' pelo ID real do chamado que está sendo atualizado

    fetch(`http://192.168.0.39:3010/call/${callId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        Authorization: token,
      },
      body: JSON.stringify(updatedCall),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Chamado atualizado:', data);
        onUpdate(data); // Chame a função onUpdate passando os dados atualizados do chamado
        onClose(); // Feche a sobreposição
      })
      .catch((error) => {
        console.error('Erro ao atualizar chamado:', error);
      })
      .finally(() => {
        setIsLoading(false);
      });
  };


  const handleCancel = () => {
    // Feche a sobreposição sem fazer nenhuma atualização
    onClose();
  };

  return (
    <div className="update-overlay">
      <div className="update-form-container">
        <h2>Atualizar Chamado</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="updatedTitle">Título:</label>
            <input
              type="text"
              id="updatedTitle"
              value={updatedTitle}
              onChange={(e) => setUpdatedTitle(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="updatedComment">Comentário:</label>
            <input
              type="text"
              id="updatedComment"
              value={updatedComment}
              onChange={(e) => setUpdatedComment(e.target.value)}
            />
          </div>
          {/* ...outros campos de atualização */}
          <div className="button-group">
            <button type="submit">Atualizar</button>
            <button type="button" onClick={handleCancel}>Cancelar</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default UpdateOverlay;
