import React, { useState, useEffect, useContext } from 'react';
import NavBar from '../components/navBar';
import { CallContext } from '../context/context';
import UpdateOverlay from '../components/updateCallPage'; 
import './callPageStyle.css';

const CallPage = () => {
  const [title, setTitle] = useState('');
  const [comment, setComment] = useState('');
  const [status, setStatus] = useState('');
  const [priority, setPriority] = useState('');
  const { user, setUser, name, setName } = useContext(CallContext);

  const [calls, setCalls] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);
  const [showUpdateOverlay, setShowUpdateOverlay] = useState(false);
  const [selectedCallId, setSelectedCallId] = useState(null); // Novo estado para armazenar o ID do chamado selecionado

  const handleDelete = (callId) => {
    const token = localStorage.getItem('token');

    fetch(`http://192.168.0.39:3010/call/${callId}`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
        Authorization: token,
      },
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Chamado excluído:', data);
        setCalls((prevCalls) => prevCalls.filter((call) => call.id !== callId));
      })
      .catch((error) => console.error('Erro ao excluir chamado:', error));
  };

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem('user'));
    setUser(userData);
  }, [setUser]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    fetch('http://192.168.0.39:3010/call', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        Authorization: token,
      },
    })
      .then((response) => response.json())
      .then((data) => setCalls(data.calls))
      .catch((error) => console.error('Erro ao buscar chamados:', error));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    const newCall = {
      title: title,
      comment: comment,
      status: status,
      priority: priority,
      user: name,
    };

    const token = localStorage.getItem('token');
    console.log(token);

    setIsLoading(true);

    fetch('http://192.168.0.39:3010/call', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: token,
      },
      body: JSON.stringify(newCall),
    })
      .then((response) => {
        console.log(response);
        return response.json();
      })
      .then((data) => {
        console.log('Chamado cadastrado:', data);
        console.log(newCall);
        if (user) {
          setCalls((prevCalls) => [
            ...prevCalls,
            { ...newCall, id: data.id, user: data.name },
          ]);
        } else {
          console.log('Usuário não está definido.');
        }
        setShowSuccessMessage(true);
      })
      .catch((error) => {
        console.error('Erro ao cadastrar chamado', error.response);
      })
      .finally(() => {
        setIsLoading(false);
      });

    setTitle('');
    setComment('');
    setStatus('');
    setPriority('');
  };

  useEffect(() => {
    if (showSuccessMessage) {
      setShowSuccessMessage(false);
      window.location.reload();
    }
  }, [showSuccessMessage]);

  const handleUpdate = (callId) => {
    setSelectedCallId(callId);
    setShowUpdateOverlay(true);
  };

  const updateCall = (updatedCall) => {
    setCalls((prevCalls) =>
      prevCalls.map((call) =>
        call.id === updatedCall.id ? updatedCall : call
      )
    );
  };

  return (
    <div>
      {/* Resto do código... */}

      {showUpdateOverlay && (
        <div className="overlay">
          <UpdateOverlay
            onClose={() => setShowUpdateOverlay(false)}
            onUpdate={updateCall} // Passando a função updateCall como prop para o componente UpdateOverlay
            callId={selectedCallId} // Passando o ID do chamado selecionado como prop para o componente UpdateOverlay
          />
        </div>
      )}
    </div>
  );
};

export default CallPage;
