import React, { useState, useEffect, useContext } from 'react';
import NavBar from '../components/navBar';
import { CallContext } from '../context/context';
import UpdateOverlay from '../components/updateCallPage';
import './callPageStyle.css';

const CallPage = ({ onUpdate, onClose }) => {
  const [title, setTitle] = useState('');
  const [comment, setComment] = useState('');
  const [status, setStatus] = useState('');
  const [priority, setPriority] = useState('');
  const { user, setUser, name, setName } = useContext(CallContext);

  const [calls, setCalls] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);
  const [showUpdateOverlay, setShowUpdateOverlay] = useState(false);
  const [selectedCallId, setSelectedCallId] = useState(null);

  const handleDelete = (callId) => {
    const token = localStorage.getItem('token');

    fetch(`http://localhost:3010/call/${callId}`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
        Authorization: token,
      },
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Chamado excluído:', data);
        setCalls((prevCalls) => prevCalls.filter((call) => call.id !== callId));
      })
      .catch((error) => console.error('Erro ao excluir chamado:', error));
  };

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem('user'));
    setUser(userData);
  }, [setUser]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    fetch('http://localhost:3010/call', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        Authorization: token,
      },
    })
      .then((response) => response.json())
      .then((data) => setCalls(data.calls))
      .catch((error) => console.error('Erro ao buscar chamados:', error));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    const newCall = {
      title: title,
      comment: comment,
      status: status,
      priority: priority,
      user: name,
    };

    const token = localStorage.getItem('token');
    console.log(token);

    setIsLoading(true);

    fetch('http://localhost:3010/call', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: token,
      },
      body: JSON.stringify(newCall),
    })
      .then((response) => {
        console.log(response);
        return response.json();
      })
      .then((data) => {
        console.log('Chamado cadastrado:', data);
        console.log(newCall);
        if (user) {
          setCalls((prevCalls) => [
            ...prevCalls,
            { ...newCall, id: data.id, user: data.name },
          ]);
        } else {
          console.log('Usuário não está definido.');
        }
        setShowSuccessMessage(true);
      })
      .catch((error) => {
        console.error('Erro ao cadastrar chamado', error.response);
      })
      .finally(() => {
        setIsLoading(false);
      });

    setTitle('');
    setComment('');
    setStatus('');
    setPriority('');
  };

  useEffect(() => {
    if (showSuccessMessage) {
      setShowSuccessMessage(false);
      window.location.reload();
    }
  }, [showSuccessMessage]);

  const handleUpdate = (callId) => {
    setSelectedCallId(callId);
    setShowUpdateOverlay(true);
  };

  return (
    <div>
      <NavBar />
      <div className="container">
        <div className="call-page">
          <div className="call-form">
            <h2>Cadastrar Chamado</h2>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Título</label>
                <input
                  type="text"
                  className="form-control"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label>Comentário</label>
                <textarea
                  className="form-control"
                  value={comment}
                  onChange={(e) => setComment(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label>Status</label>
                <select
                  className="form-control"
                  value={status}
                  onChange={(e) => setStatus(e.target.value)}
                  required
                >
                  <option value="">Selecione</option>
                  <option value="Aberto">Aberto</option>
                  <option value="Em andamento">Em andamento</option>
                  <option value="Fechado">Fechado</option>
                </select>
              </div>
              <div className="form-group">
                <label>Prioridade</label>
                <select
                  className="form-control"
                  value={priority}
                  onChange={(e) => setPriority(e.target.value)}
                  required
                >
                  <option value="">Selecione</option>
                  <option value="Baixa">Baixa</option>
                  <option value="Média">Média</option>
                  <option value="Alta">Alta</option>
                </select>
              </div>
              <button type="submit" className="btn btn-primary">
                {isLoading ? 'Enviando...' : 'Cadastrar'}
              </button>
            </form>
            {showSuccessMessage && (
              <div className="success-message">Chamado cadastrado com sucesso!</div>
            )}
          </div>
          <div className="call-list">
            <h2>Chamados</h2>
            {calls.map((call) => (
              <div key={call.id} className="call-item">
                <div className="call-details">
                  <h3>{call.title}</h3>
                  <p>{call.comment}</p>
                  <p>{`Status: ${call.status}`}</p>
                  <p>{`Prioridade: ${call.priority}`}</p>
                  <p>{`Usuário: ${call.user}`}</p>
                </div>
                <div className="call-actions">
                  <button
                    className="btn btn-secondary"
                    onClick={() => handleUpdate(call.id)}
                  >
                    Atualizar
                  </button>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleDelete(call.id)}
                  >
                    Excluir
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
      {showUpdateOverlay && (
        <UpdateOverlay callId={selectedCallId} onUpdate={onUpdate} onClose={onClose} />
      )}
    </div>
  );
};

export default CallPage;
